function change_masory() {
    $(".products").masonry({
        itemSelector: ".col-lg-4",
        percentPosition: !0
    })
}
$(function() {
    var e = $("#iframe").attr("src");
    function t() {
        $(".overlay-popup").fadeOut(),
        $("#iframe").attr("src", e + "&autoplay=0")
    }
    $("#btn-video").on("click", function(t) {
        t.preventDefault(),
        $(".overlay-popup").fadeIn(),
        $("#iframe").attr("src", e + "&autoplay=1")
    }),
    $("#btn-close").on("click", function(e) {
        e.preventDefault(),
        t()
    }),
    $(".overlay-popup").on("click", function(e) {
        e.target === this && t()
    }),
    $(".list_parent").on("click", function() {
        $(this).hasClass("active") ? ($(this).removeClass("active"),
        change_masory()) : ($(".list_parent").removeClass("active"),
        $(this).addClass("active"),
        change_masory())
    }),
    change_masory()
});
